tinymce.PluginManager.add('test', function(editor) {
    function test() {
        editor.focus(true);
        editor.insertContent('[skd_quangcao][/skd_quangcao]')
    }
    editor.ui.registry.addButton('test', {
        text: 'Quảng cáo',
        tooltip: 'Quảng cáo',
        onAction: test
    });
});